import React, { Component } from 'react'
import './style.css'
import dataGlasses from '../Data/dataGlasses.json';

export default class TryGlassesOn extends Component {
  renderGlassesList = () => {
    return dataGlasses.map((glassesItem, index) => {
      return <img className='ml-2 p-2 border border-width-1' key={index} style={{width:'100px', cursor:'pointer'}} src={glassesItem.url}/>
    })
  }

  render() {
    const styleGlasses = {
      width: '150px',
      top: '66px',
      right: '69px',
      opacity: '0.8'
    }

    const infoGlasses = {
      width: '250px',
      top: '200px',
      left: '270px',
      paddingLeft: '15px',
      backgroundColor: 'rgba(255,127,0,.5)',
      textAlign: 'left',
      height: '105px'
    }


    return (
      <div>
        <h3 className='txt_background txt_color'>Doan Hoang Thinh</h3>
      {/* <div style={{ backgroundImage: 'url(./glassesImage/background.jpg)', backgroundSize: '2000px', minHeight: '2000px' }}> */}
      <div >
        <div style={{ backgroundColor: 'rgba(0,0,0,.8)', minHeight: '2000px' }}>
          <h3 style={{ backgroundColor: 'rgba(0, 0 ,0,.3)' }} className="text-center text-light p-5">TRY GLASSES APP ONLINE</h3>
          <div className="container">
            <div className="row mt-5 text-center">
              <div className="col-6">
                <div className="position-relative">
                  <img className="position-absolute" style={{ width: '250px' }} src="./glassesImage/model.jpg" alt='model.jpg' />
                  <img style={styleGlasses} className="position-absolute" src="./glassesImage/v1.png" alt='v1'/>
                  <div style={infoGlasses} className="position-relative ">
                    <span style={{ color: '#AB82FF', fontSize: '17px' }} className="font-weight-bold">Ten</span> <br />
                    <span style={{ fontSize: '14px', paddingRight: '5px', fontWeight: '400' }}>Lop</span>
                  </div>
                </div>
              </div>
              <div className="col-6">
                <img style={{ width: '250px' }} src="./glassesImage/model.jpg" alt='model.jpg' />

              </div>
            </div>
          </div>
          {/* Div chứa các kính được chọn */}
          <div className="bg-light container text-center mt-5 d-flex justify-content-center p-5">
            {this.renderGlassesList()}
          </div>
        </div>
      </div >
      </div>
    )
  }
}
