import React, { Component } from 'react'

export default class  extends Component {
  // Declare method handle event when button is clicked
  handleClick = () => {
    alert("hello");
  }

  render() {
    return (
      <div class="text-center">
        <button onClick={this.handleClick}>Click me</button>
      </div>
    )
  }
}
